package loops;

public class naturalnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum=0;
		System.out.println("The n natural numbers:");
		for(int i=1;i<=10;i++)
		{
			System.out.print(i);
			sum=sum+i;
		}
		System.out.println("\n Sum of the Number:"+sum);

	}

}
