package loops;

public class oddnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int odd=50;
		System.out.println("List of odd numbers 1 to 50:");
		for(int i=1;i<=odd;i++)
		{
			if(i%2!=0)
			{
				System.out.println(i+"");
			}
				
		}

	}

}
